import * as React from 'react';
import { StyleSheet, Text, View } from 'react-native';

export default class Contador extends React.Component {

    constructor(){
        super();
        this.state = {
            contador: 0,
        };
    }

    Decrementar(){
        this.setState({
            contador: this.state.contador - 1,
        });
    }

    Incrementar(){
        this.setState({
            contador: this.state.contador + 1,
        });
    }

    render(){
        return(
            <view></view>
        )
    }

}
